SnowBallConfig = {}
SnowBallConfig.snowBalls = {}

function SnowBallConfig:init(config)
    for i, snowball in pairs(config) do
        local item = {}
        item.id = snowball.id
        item.name = snowball.name
        item.desc = snowball.desc
        item.image = snowball.image
        item.damageToF = tonumber(snowball.level)
        item.damageToP = tonumber(snowball.value)
        item.damageToSF = tonumber(snowball.nextValue)
        item.price = tonumber(snowball.price)
        self.snowBalls[item.id] = item
    end
end

function SnowBallConfig:getSnowballByLevel(level)
    for _, snowball in pairs(self.snowBalls) do
        if snowball.id == tostring(level) then
            return snowball
        end
    end
    return nil
end

