RewardUtil = {}

function RewardUtil:sortPlayerRank()
    local players = PlayerManager:copyPlayers()
    if #players == 0 then
        return players
    end
    table.sort(players, function(a, b)
        if a.score ~= b.score then
            return a.score > b.score
        end
        return a.userId > b.userId
    end)

    return players
end

function RewardUtil:getPlayerRank(player)
    local players = self:sortPlayerRank()
    local rank = 0
    for i, v in pairs(players) do
        rank = rank + 1
        if v.rakssid == player.rakssid then
            return rank
        end
    end
    return rank
end

function RewardUtil:getWinnerTeam()
    local winner = 0
    local lifeTeams = {}
    for _, team in pairs(GameMatch.Teams) do
        if team:isDeath() == false then
            lifeTeams[#lifeTeams + 1] = team
        end
    end
    if #lifeTeams == 1 then
        winner = lifeTeams[1].ID
    end
    return winner
end

function RewardUtil:doReward(players)
    if players == nil then
        return
    end
    local winner = self:getWinnerTeam()
    for rank, player in pairs(players) do
        RewardManager:addRewardQueue(player.userId, rank)
        UserExpManager:addUserExp(player.userId, winner == player:getTeamId(), 4)
    end
    RewardManager:getQueueReward(function(data, winner)
        self:sendGameSettlement(winner)
    end, winner)
end

function RewardUtil:doReport(players)
    for rank, player in pairs(players) do
        if player.isReport == false then
            ReportManager:reportUserData(player.userId, player.kills, rank, 1)
            player.isReport = true
        end
    end
end

function RewardUtil:sendGameSettlement(winner)
    local Players = self:sortPlayerRank()
    local players = {}

    for rank, player in pairs(Players) do
        players[rank] = self:newSettlementItem(player, rank, winner)
    end

    for rank, player in pairs(Players) do
        local result = {}
        result.players = players
        result.own = self:newSettlementItem(player, rank, winner)
        HostApi.sendGameSettlement(player.rakssid, json.encode(result), true)
    end
end

function RewardUtil:newSettlementItem(player, rank, winner)
    local item = {}
    item.name = player.name
    item.rank = rank
    item.points = player.scorePoints
    item.gold = player.gold
    item.available = player.available
    item.hasGet = player.hasGet
    item.vip = player.vip
    item.kills = player.kills
    item.adSwitch = player.adSwitch or 0
    if item.gold <= 0 or player.isSingleReward then
        item.adSwitch = 0
    end
    if winner == 0 then
        item.isWin = 2
    else
        if winner == player:getTeamId() then
            item.isWin = 1
        else
            item.isWin = 0
        end
    end
    return item
end

return RewardUtil