RankConfig = {}
RankConfig.ranks = {}

function RankConfig:init(config)
    self:initRankConfig(config)
end

function RankConfig:initRankConfig(ranks)
    if type(ranks) ~= 'table' then
        return 
    end
    for i, rank in pairs(ranks) do
        local item = {}
        item.id = #item + 1
        item.name = rank.npc.name
        item.pos = VectorUtil.newVector3(tonumber(rank.npc.pos[1]), tonumber(rank.npc.pos[2]), tonumber(rank.npc.pos[3]))
        item.yaw = tonumber(rank.npc.yaw)
        self.ranks[item.id] = item
    end
end

return RankConfig