require "data.GamePlayer"

RoleConfig = {}
RoleConfig.seek = {}
RoleConfig.hide = {}

function RoleConfig:init(config)
    self:initRole(config)
end

function RoleConfig:initRole(config)
    for id, role in pairs(config) do
        local item = {}
        item.id = id
        item.name = role.name
        item.skillId = tonumber(role.skillId)
        item.hp = tonumber(role.hp)
        item.defense = tonumber(role.defense)
        item.gunId = StringUtil.split(role.gunId, "#")
        item.speedlevel = tonumber(role.speedlevel)
        if role.name == "Seek" then
            self.seek = item
        else
            self.hide = item
        end
    end
end

function RoleConfig:getConfigByRole(role)
    if role == GamePlayer.ROLE_SEEK then
        return self.seek
    else
        return self.hide
    end
end

return RoleConfig
