require "skill.BaseSkill"

AddAttackSkill = class("AddAttackSkill", BaseSkill)
AddAttackSkill.AttributeType = {
    Duration = "6041",
    AddAttack = "6042"
}

function AddAttackSkill:onInitAttributeFinish()
    self.players = {}
    self.duration = self.duration + self:getAttributeById(AddAttackSkill.AttributeType.Duration)
    self.damage = self.damage + self:getAttributeById(AddAttackSkill.AttributeType.AddAttack)
end

function AddAttackSkill:createSkillEffect(name, time)
    local creator = self:getCreator()
    if #name > 6 and time > 0 then
        local add = self:getAttributeById(AddAttackSkill.AttributeType.Duration) * 1000
        creator:addCustomEffect("AddAttackSkill", name, time + add)
    end
end

function AddAttackSkill:onPlayerEffected(player)
    if self.players[tostring(player.userId)] then
        return
    end
    player.addDamage = player.addDamage + self.damage
    self.players[tostring(player.userId)] = true
end

function AddAttackSkill:onDestroy()
    for userId, _ in pairs(self.players) do
        local player = PlayerManager:getPlayerByUserId(userId)
        if player ~= nil then
            player.addDamage = player.addDamage - self.damage
        end
    end
    self.players = {}
end

return AddAttackSkill