require "skill.BaseSkill"

AddSpeedSkill = class("AddSpeedSkill", BaseSkill)
AddSpeedSkill.AttributeType = {
    Duration = "6031",
    AddSpeed = "6032"
}

function AddSpeedSkill:onInitAttributeFinish()
    self.players = {}
    self.duration = self.duration + self:getAttributeById(AddSpeedSkill.AttributeType.Duration)
    self.damage = self.damage + self:getAttributeById(AddSpeedSkill.AttributeType.AddSpeed)
end

function AddSpeedSkill:createSkillEffect(name, time)
    local creator = self:getCreator()
    if #name > 6 and time > 0 then
        local add = self:getAttributeById(AddSpeedSkill.AttributeType.Duration) * 1000
        creator:addCustomEffect("AddSpeedSkill", name, time + add)
    end
end

function AddSpeedSkill:onPlayerEffected(player)
    if self.players[tostring(player.userId)] then
        return
    end
    player.addSpeed = player.addSpeed + self.damage
    player:setSpeedAddition(player.addSpeed)
    self.players[tostring(player.userId)] = true
end

function AddSpeedSkill:onDestroy()
    for userId, _ in pairs(self.players) do
        local player = PlayerManager:getPlayerByUserId(userId)
        if player ~= nil then
            player.addSpeed = player.addSpeed - self.damage
            player:setSpeedAddition(player.addSpeed)
        end
    end
    self.players = {}
end

return AddSpeedSkill