require "skill.BaseSkill"

AdrenalineSkill = class("AdrenalineSkill", BaseSkill)
AdrenalineSkill.AttributeType = {
    Duration = "6061",
    AddSpeed = "6062",
    AddDamage = "6063"
}

function AdrenalineSkill:onInitAttributeFinish()
    self.players = {}
    self.duration = self.duration + self:getAttributeById(AdrenalineSkill.AttributeType.Duration)
    self.speed = 250 + self:getAttributeById(AdrenalineSkill.AttributeType.AddSpeed)
    self.damage = self.damage + self:getAttributeById(AdrenalineSkill.AttributeType.AddDamage)
end

function AdrenalineSkill:createSkillEffect(name, time)
    local creator = self:getCreator()
    if #name > 6 and time > 0 then
        creator:addCustomEffect("AdrenalineSkill", name, time)
    end
end

function AdrenalineSkill:onPlayerEffected(player)
    if self.players[tostring(player.userId)] then
        return
    end
    player.addDamage = player.addDamage + self.damage
    player.addSpeed = player.addSpeed + self.speed
    player:setSpeedAddition(player.addSpeed)
    self.players[tostring(player.userId)] = true
end

function AdrenalineSkill:onDestroy()
    for userId, _ in pairs(self.players) do
        local player = PlayerManager:getPlayerByUserId(userId)
        if player ~= nil then
            player.addDamage = player.addDamage - self.damage
            player.addSpeed = player.addSpeed - self.speed
            player:setSpeedAddition(player.addSpeed)
        end
    end
    self.players = {}
end

return AdrenalineSkill