require "skill.BaseSkill"

FlySkill = class("FlySkill", BaseSkill)
FlySkill.AttributeType = {
    Duration = "6091"
}

function FlySkill:onInitAttributeFinish()
    self.players = {}
    self.duration = self.duration + self:getAttributeById(FlySkill.AttributeType.Duration)
end

function FlySkill:createSkillEffect(name, time)
    local creator = self:getCreator()
    if #name > 6 and time > 0 then
        local add = self:getAttributeById(FlySkill.AttributeType.Duration) * 1000
        creator:addCustomEffect("FlySkill", name, time + add)
    end
end

function FlySkill:onPlayerEffected(player)
    if self.players[tostring(player.userId)] then
        return
    end
    player:setAllowFlying(true)
    self.players[tostring(player.userId)] = true
end

function FlySkill:onDestroy()
    for userId, _ in pairs(self.players) do
        local player = PlayerManager:getPlayerByUserId(userId)
        if player ~= nil then
            player:setAllowFlying(false)
        end
    end
    self.players = {}
end

return FlySkill