EntityListener = {}

function EntityListener:init()
    BlockmanEmptyMoveEvent.registerCallBack(self.onBlockmanEmptyMove)
    EntityItemSpawnEvent.registerCallBack(self.onPlayerSpawnDropItem)
end

function EntityListener.onPlayerSpawnDropItem(itemId, itemDamage, str)
    return false
end

function EntityListener.onBlockmanEmptyMove(entityId, position)
    local car = GameManager:getCarByEntityId(entityId)
    if car then
        car:onMove(position)
    end
end

return EntityListener
